import { async, TestBed } from '@angular/core/testing';
import { DataService } from '../services/data.service';
import { PostsService } from '../services/posts.service';
import { HomeComponent } from './home.component';
import { NO_ERRORS_SCHEMA } from '@angular/core';
import { RouterTestingModule } from '@angular/router/testing';
import { HttpClientTestingModule } from '@angular/common/http/testing';
import { HttpClientModule } from '@angular/common/http';
import { BrowserModule, By } from '@angular/platform-browser';
import { AppRoutingModule } from '../app-routing.module';
import { FlexLayoutModule } from '@angular/flex-layout';
import { MatInputModule } from '@angular/material/input';
import { MatSnackBarModule } from '@angular/material/snack-bar';
import { BrowserAnimationsModule } from '@angular/platform-browser/animations';
import { MatButtonModule } from '@angular/material/button';
import { FormsModule } from '@angular/forms';
describe('HomeComponent', () => {
    let component;
    let fixture;
    beforeEach(async () => {
        await TestBed.configureTestingModule({
            imports: [
                RouterTestingModule,
                HttpClientTestingModule,
                HttpClientModule,
                BrowserModule,
                AppRoutingModule,
                FlexLayoutModule,
                MatInputModule,
                MatSnackBarModule,
                BrowserAnimationsModule,
                MatButtonModule,
                FormsModule,
            ],
            declarations: [HomeComponent],
            providers: [DataService, PostsService],
            schemas: [
                NO_ERRORS_SCHEMA
            ]
        })
            .compileComponents();
    });
    beforeEach(() => {
        fixture = TestBed.createComponent(HomeComponent);
        component = fixture.componentInstance;
        fixture.detectChanges();
    });
    it('should be created', () => {
        expect(component).toBeTruthy();
    });
    it(`should have as title 'Angular Application with NodeJs'`, () => {
        const fixture = TestBed.createComponent(HomeComponent);
        const app = fixture.debugElement.componentInstance;
        expect(app.title).toEqual('Angular Application with NodeJs');
    });
    // it('button click event one way', async(() => {
    //   spyOn(component, 'sendID');
    //   let button = fixture.debugElement.nativeElement.querySelector('button');
    //   button.click(); // you can use     btn.triggerEventHandler('click', null);
    //   fixture.detectChanges();
    //   fixture.whenStable().then(() => {
    //     expect(component.sendID).toHaveBeenCalled();
    //   });
    // }));
    it('should be ok', async(() => {
        let fixture = TestBed.createComponent(HomeComponent);
        fixture.detectChanges();
        fixture.whenStable().then(() => {
            let input = fixture.debugElement.query(By.css('input'));
            let el = input.nativeElement;
            expect(el.value).toBe('peeskillet');
            el.value = 'someValue';
            el.dispatchEvent(new Event('input'));
            expect(fixture.componentInstance.id).toBe('2');
        });
    }));
});
